#include "Logger.h"  // Include the Logger header file#include <iostream>#include <fstream>  // Fixes "ifstream is incomplete"#include <string>   // Fixes "getline is undefined"#include <cassert>  // Fixes "assert is undefined"using namespace std;void TestLogger() {    Logger log("TestLog.txt");    assert(log.IsLogOpen() == true);    std::cout << "? Logger file open test passed.\n";    log.StartLog(100.0);    log.WriteLog("Player won $50.");    log.CloseLog("End of session.");    // Check if file exists    std::ifstream file("TestLog.txt");    assert(file.good());    std::cout << "? Logger file creation test passed.\n";    // Check if content was written    std::string line;    bool found = false;    while (getline(file, line)) {        if (line.find("Player won $50.") != std::string::npos) {            found = true;            break;        }    }    file.close();    assert(found == true);    std::cout << "✔ Logger file open test passed.\n";}int main() {    TestLogger();    std::cout << "All Logger.h tests passed! ??\n";    return 0;}